import React from 'react';
import './Post.css';

class Post extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      text: ""
    }
  }

  componentWillUnmount() {
    //...
  }

  newText = (ev) => this.setState({text: ev.target.value })

  render() {
    return (
      <article >
        <h2 className={(this.props.claseCheck) ? 'true' : 'false'}> {this.props.title}</h2>
        <div className="editLine">
            <div className="" onClick={() => this.props.fnBorrar(this.props.keyy)} >Delete</div>
            <div onClick={() => this.props.fnEdit(this.props.keyy, this.state.text )} >Edit</div>
            <input type="text" value="Edita aquí" placeholder="Edita" onChange={this.newText}/>
            <input value="¿Ya tienes hecha la tarea?" onClick={ ()=>this.props.tareaHecha(this.props.keyy) } type="checkbox" /> 
        </div>
      </article>
    )
  }
}

export default Post;
