import React from 'react';
import './Post.css';
import { useEffect, useContext } from 'react';


const Post = props => {

  const [text, setText] = useState("");
  const [priority, setPriority] = useState("ninguna");



  newText = (ev) => setText( { text: ev.target.value } )
  newPriority = (el) => setPriority( { priority: el.target.value } )


    return (
      <article >
        <h2 className={`${priority} ${props.claseCheck}`} > {props.title}</h2>
        <div className="editLine">
            <div className="btn" onClick={() => props.fnBorrar(props.keyy)} >Delete</div>
            <div className="btn" onClick={()=> props.btnEditNewPost(props.keyy)}>Edit</div>
            {/* <select onChange={this.newPriority} className="selectBtn"  onClick={() => props.editPriority(props.keyy, props.statePriority)}> */}
            <select className="selectBtn"  onClick={() => props.editPriority(props.keyy, props.statePriority)}>
                  <option className="ninguna" value="ninguna">Prioridad</option>
                  <option value="baja">Baja</option>
                  <option  value="media">Media</option>
                  <option  value="alta">Alta</option>
            </select>
            <input className="done" onClick={ ()=>props.tareaHecha(props.keyy) } type="checkbox" /> 
        </div>
      </article>
    )
}

export default Post;
