import React from 'react';
import './Main.css';
//import spinner from './spinner.gif';

import Post from '../Post/Post';
import Form from '../Form/Form';
import getPosts from '../../data/dataProvider';



// ----------- STATE ITNITIAL
class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      posts: [],
      user: this.props.user || "",
      limit: 10,
      insert: true
    }
  }

  async componentDidMount() {
    const newPosts = await getPosts()
    this.setState({ posts: [...this.state.posts, ...newPosts] });
  }



// ---------- CONEXIÓN CON EL COMPONENTE POST
    drawPosts = () => {
      console.log("draw", this.state.posts);
      if (this.state.posts.length > 0) {
          return this.state.posts.map((item, i) =>
          <Post title={item.title} fnBorrar={this.deletePosts} keyy={i} fnEdit={this.editPosts} fnFlag={this.loadPosts}/>
        )
      }
      return <h4>No hay datos</h4>
    }


// ---------- FUNCIÓN ELIMINAR AL HACER CLIC EN EL BOTON de POST
      deletePosts = (el) => { 
        console.log(el);
        if(this.state.posts.length > 0){
          const task = this.state.posts.filter((index, i )=> i !== el);
          this.setState({posts: task})
        }
      }



// ---------- FUNCIÓN EDITAR AL HACER CLIC EN EL BOTON de POST
  editPosts = (i, newTitle) => {
    console.log(i)
    if(this.state.posts.length > 0){
      const newTitles = [...this.state.posts];  // Copy of an array
      newTitles[i].title = newTitle             // Modificar la copia con la i y el nuevo título
      this.setState({ posts: newTitles })       // Se sustituye el nuevo título
    }
    this.setState({insert: true})               // Cambiar el estado de INSERT
  }

  loadPosts = (i) => {
    this.setState({insert: false})
  }

  addPost = (title) => { 
    this.setState({
      posts: [...this.state.posts, { title }]
    });
  }

  drawForm = () => {
    if(this.state.insert || this.state.insert == false)         // OJO AQuI QUE NO TIENE QUE SER == false
      return <Form addPost={this.addPost} editPosts={this.editPosts} state={this.state.insert} />
      // return <FormEdit editPosts={this.editPosts} />
  }

  render() {
    return (
      <main>
        <h1>Hola, {this.state.user ? this.state.user : "¡qué pasa Chape!"}</h1>
        {/* <Form addPost={this.addPost} editPosts={this.editPosts} state={this.state.insert} /> */}
        {this.drawForm()}
        {this.drawPosts()}
      </main>
    )
  }
}

export default Main;
