import React from 'react';
import './Post.css';

class Post extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      text: ""
    }
  }

  componentWillUnmount() {
    //...
  }

  newText = (ev) => this.setState({text: ev.target.value })

  render() {
    return (
      <article >
        <h2 className={(this.props.claseCheck) ? 'true' : 'false'}> {this.props.title}</h2>
        <div className="editLine">
            <div className="btn" onClick={() => this.props.fnBorrar(this.props.keyy)} >Delete</div>
            <div className="btn" onClick={() => this.props.fnEdit(this.props.keyy, this.state.text )} >Edit</div>
            <input className="editInput" type="text"  placeholder="Edita" onChange={this.newText}/>
            <select className="selectBtn" onClick={() => this.props.editPriority(this.props.keyy)}>
                  <option className="selectBtn" selected value="baja">Baja</option>
                  <option className="selectBtn" value="alta">Alta</option>
            </select>
            <input className="placeholder" onClick={ ()=>this.props.tareaHecha(this.props.keyy) } type="checkbox" /> 
        </div>
      </article>
    )
  }
}

export default Post;
