import React from 'react';
import './Main.css';
//import spinner from './spinner.gif';

import Post from '../Post/Post';
import Form from '../Form/Form';
import getPosts from '../../data/dataProvider';

class Main extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      posts: [],
      user: this.props.user || "",
      limit: 10
    }


    // ES5 Version
    // this.addPost = this.addPost.bind(this);
  }

  async componentDidMount() {
    const newPosts = await getPosts()
    this.setState({ posts: [...this.state.posts, ...newPosts] });
  }

  // async componentDidDelete() {
    //   const delPosts = await deletePost()
    //   this.setState({ posts: [...this.state.posts, ...newPosts] });
    // }

    drawPosts = () => {
      console.log("draw", this.state.posts);
      if (this.state.posts.length > 0) {
        console.log("PRIMER CONSOLE", this.state.posts);
          return this.state.posts.map((item, i) =>
          <Post title={item.title} excerpt={item.excerpt} fnBorrar={this.deletePosts} keyy={i} fnEdit={this.editPosts}/>
        )
      }
      return <h4>No hay datos</h4>
    }


      // ---------- FUNCIÓN ELIMINAR AL HACER CLIC POR KEY
      deletePosts = (el) => { 
        console.log(el);
        if(this.state.posts.length > 0){
          const task = this.state.posts.filter((index, i )=> i !== el);
          this.setState({posts: task})
        }
      }

// ---------- FUNCIÓN EDITAR AL HACER CLIC EN EL BOTON
  editPosts = (i, newTitle) => {
    console.log(i)
    if(this.state.posts.length > 0){
      // const edit = this.state.posts.splice(i, 1, newTitle) // Hay que buscar el EDIT DE DONDE SALE
      const newPosts = [...this.state.posts]; // Copy of an array
      newPosts[i].title = newTitle
      this.setState({ posts: newPosts })
    }
  }

  //addPost() { // ES5 Version
  addPost = (title, excerpt) => { // ES6 Version
    this.setState({
      posts: [...this.state.posts, { title, excerpt }]
    });
  }

  render() {
    return (
      <main>
        <h1>Hola, {this.state.user ? this.state.user : "¡qué pasa Chape!"}</h1>
        {/*<h1>Hola, {this.state.user || "Anónimo"}</h1>*/}
        <Form addPost={this.addPost} />
        {this.drawPosts()}
      </main>
    )
  }
}

export default Main;
