import React from 'react';
import './Form.css';

class Form extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      title: (this.props.state.insert) ? ( <input type="text" value={this.state.title}/>) : (<input type="text" value=""/>)
    }
  }

  handleTitle = event => this.setState({ title: event.target.value })

  handleNewPost = event => {
    event.preventDefault()
    this.props.addPost(this.state.title)
  }

  render() {
    return (
      <form className="titleLine">
        <input className="editInput" type="text" onChange={this.handleTitle} />
        <div className="btn" onClick={this.handleNewPost}>Crear tarea</div>
      </form>
    )
  }
}

export default Form;
